import React, { useState } from "react";

import MoviesList from "./components/MoviesList";
import "./App.css";

function App() {
  const [movies, setMovies] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  // an alternative to .then blocks is
  async function fetchMoviesHandler() {
    setIsLoading(true);
    setError(null);

    // if .then, then use .catch()
    try {
      const response = await fetch("https://swapi.dev/api/films/");
      if (!response.ok) {
        throw new Error('Somethign went wrong!');
      }

      const data = await response.json();
      // function fetchMoviesHandler() {
      //   fetch('https://swapi.dev/api/films/')
      //     .then((response) => {
      //       // transforms json data into JS object
      //       return response.json();
      //     })
      // .then((data) => {
      const transformedMovies = data.results.map((movieData) => {
        return {
          id: movieData.episode_id,
          title: movieData.title,
          openingText: movieData.opening_crawl,
          releaseDate: movieData.release_date,
        };
      });
      setMovies(transformedMovies);
      // });
    } catch (error) {
      setError(error.message);
    }
    setIsLoading(false);
  }

  let content = <p>Found no movies</p>;

  if (movies.length > 0) {
    content = <MoviesList movies={movies} />;
  }

  if (error) {
    content = <p>{error}</p>;
  }

  if (isLoading) {
    content = <p>Loading...</p>;
  }

  return (
    <React.Fragment>
      <section>
        <button onClick={fetchMoviesHandler}>Fetch Movies</button>
      </section>
      <section>
        {/* if the data is not loading, i.e .finished loading, show movies list */}
        {!isLoading && movies.length === 0 && !error && <p>No movies to show.</p>}
      </section>
    </React.Fragment>
  );
}

export default App;
